var hasUint32ArraySupport = require('@stdlib/assert-has-uint32array-support');
var builtin = require('./uint32array.js');
var polyfill = require('./polyfill.js');

// MAIN //

var ctor;
if (hasUint32ArraySupport()) {
  ctor = builtin;
} else {
  ctor = polyfill;
}

// EXPORTS //

module.exports = ctor;

